def HAMM(nts1, nts2):
    """Counting Point Mutations"""
    length = min(len(nts1), len(nts2))
    hamming_distance = 0
    for i in range(length):
        if nts1[i] != nts2[i]:
            hamming_distance += 1
    return hamming_distance

